export async function getServerSideProps( context ) {
    try {
        const slugName = context.query.slug;
        const data = await getScoreBySlug(slugName);

        return { 
            props: { 
                slugID: slugName,
                scoreVal : data.scoreData,
                telNum: context.query.tel
            }
        }
    } catch (error) {
        throw new Error("Fail fecth data to propps!");
    }
}